import React, { Component } from "react";

class Clock extends Component {
  state = {
    date: new Date(),
  };

  componentDidMount() {
    this.interval = setInterval(
      () => this.setState({ time: Date.now() }),
      1000
    );
  }

  render() {
    return <div>time: {this.state.date}</div>;
  }
}

export default Clock;
