import React, { Component } from "react";

class Clock extends Component {
  state = {
    now: new Date(),
  };

  tick() {
    this.setState((state) => ({
      now: state.now,
    }));
  }

  componentDidMount() {
    this.interval = setInterval(() => this.tick(), 1000);
  }

  render() {
    return <div>time: {this.state.now.toString()}</div>;
  }
}

export default Clock;
