import React, { Component } from "react";

class Clock extends Component {
  state = {
    date: new Date().toLocaleString(),
  };

  componentDidMount() {
    setInterval(
      () =>
        this.setState((prevState) => ({
          date: new Date().toLocaleString(),
        })),
      1000
    );
  }

  render() {
    return <div>time: {this.state.date.toString()}</div>;
  }
}

export default Clock;
